mapper.select:
SELECT

FROM
CLIENT

  AND NM_CLIENT = #{client.nmClient}
  AND KN_CLIENT = #{client.knClient}
  AND CD_EIGYO_BUSHO = #{client.cdEigyoBusho}
  AND CD_EIGYO_TANTO = #{client.cdEigyoTanto}
  AND CD_CLIENT = #{client.cdClient}
  AND KB_JIGYO = #{client.kbJigyo}
  AND KB_CLIENT1 = #{client.kbClient1}
  AND KB_CLIENT2 = #{client.kbClient2}
  AND KB_CLIENT3 = #{client.kbClient3}
  AND KB_CLIENT4 = #{client.kbClient4}
  AND FG_HANSHA = #{client.fgHansha}
  AND FG_YOSHIN = #{client.fgYoshin}
  AND NM_YOSHIN_COMMENT IS NOT NULL 
  AND NM_YOSHIN_COMMENT IS NULL 
  AND FG_DELETE = #{client.fgDelete}
  AND FG_WEB_KINSHI = #{client.fgWebKinshi}
  AND KB_WEB_KAHI = #{client.kbWebKahi}
